%% PV Block Configuration Helper\n% Run this to set recommended PV parameters\n\nmodel_name = 'model';\nload_system(model_name);\n\n% Find PV block\npv_blocks = find_system(model_name, 'MaskType', 'PV Array');\nif isempty(pv_blocks)\n    fprintf('PV block not found\\n');\n    return;\nend\n\npv_block = pv_blocks{1};\nfprintf('Configuring PV block: %s\\n', pv_block);\n\n%% Recommended parameters for 250W panel\ntry\n    set_param(pv_block, 'Voc', '37');    % Open circuit voltage\n    set_param(pv_block, 'Isc', '9.0');   % Short circuit current\n    set_param(pv_block, 'Vmp', '30');    % Voltage at max power\n    set_param(pv_block, 'Imp', '8.33');  % Current at max power\n    set_param(pv_block, 'Ns', '60');     % Number of cells in series\n    fprintf('✓ PV parameters updated\\n');\ncatch ME\n    fprintf('⚠ Could not set parameters: %s\\n', ME.message);\n    fprintf('Please set manually in block dialog\\n');\nend\n